import React from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  LightBulbIcon, 
  ShieldExclamationIcon, 
  CogIcon, 
  ScaleIcon 
} from '@heroicons/react/24/outline';

const getAgentConfig = (agentType) => {
  const configs = {
    innovator: {
      icon: LightBulbIcon,
      title: 'The Innovator',
      description: 'Champions progress & breakthrough solutions',
      colors: {
        primary: 'text-emerald-700',
        bg: 'bg-gradient-to-br from-emerald-50 to-green-50',
        border: 'border-emerald-200',
        accent: 'bg-emerald-100',
        glow: 'shadow-emerald-200/50'
      }
    },
    skeptic: {
      icon: ShieldExclamationIcon,
      title: 'The Skeptic',
      description: 'Challenges assumptions & identifies risks',
      colors: {
        primary: 'text-red-700',
        bg: 'bg-gradient-to-br from-red-50 to-pink-50',
        border: 'border-red-200',
        accent: 'bg-red-100',
        glow: 'shadow-red-200/50'
      }
    },
    engineer: {
      icon: CogIcon,
      title: 'The Engineer',
      description: 'Focuses on feasibility & implementation',
      colors: {
        primary: 'text-blue-700',
        bg: 'bg-gradient-to-br from-blue-50 to-cyan-50',
        border: 'border-blue-200',
        accent: 'bg-blue-100',
        glow: 'shadow-blue-200/50'
      }
    },
    ethicist: {
      icon: ScaleIcon,
      title: 'The Ethicist',
      description: 'Examines moral implications & social impact',
      colors: {
        primary: 'text-purple-700',
        bg: 'bg-gradient-to-br from-purple-50 to-indigo-50',
        border: 'border-purple-200',
        accent: 'bg-purple-100',
        glow: 'shadow-purple-200/50'
      }
    }
  };
  return configs[agentType] || configs.innovator;
};

const AgentPanel = ({ agent, messages = [], currentRound = 1, isActive = false }) => {
  const config = getAgentConfig(agent);
  const Icon = config.icon;
  
  const agentMessages = messages.filter(msg => 
    msg.data && msg.data.agent === agent
  ).sort((a, b) => (a.data.round || 0) - (b.data.round || 0));

  const hasMessages = agentMessages.length > 0;
  const latestMessage = agentMessages[agentMessages.length - 1];

  return (
    <motion.div 
      initial={{ opacity: 0, scale: 0.95 }}
      animate={{ opacity: 1, scale: 1 }}
      className={`
        ${config.colors.bg} 
        ${config.colors.border} 
        border-2 rounded-xl p-5 h-full min-h-[500px] 
        ${isActive ? `${config.colors.glow} shadow-xl scale-[1.02]` : 'shadow-lg'}
        transition-all duration-300 relative overflow-hidden
      `}
    >
      {/* Status Indicator */}
      {isActive && (
  <div className="absolute top-2 right-2">
    <div className={`${config.colors.accent} rounded-full px-2 py-1`}>
      <div className="flex items-center space-x-1">
        <div className="w-2 h-2 bg-current rounded-full animate-pulse"></div>
        <span className="text-xs font-medium">Thinking...</span>
      </div>
    </div>
  </div>  
)}

      {/* Header */}
      <div className="flex items-center space-x-3 mb-4">
        <div className={`${config.colors.accent} p-2 rounded-lg`}>
          <Icon className={`h-6 w-6 ${config.colors.primary}`} />
        </div>
        <div>
          <h3 className={`font-bold text-lg ${config.colors.primary}`}>
            {config.title}
          </h3>
          <p className="text-xs text-gray-600 leading-tight">
            {config.description}
          </p>
        </div>
      </div>
      
      {/* Messages */}
      <div className="space-y-3 max-h-96 overflow-y-auto scrollbar-thin scrollbar-thumb-gray-300">
        <AnimatePresence>
          {!hasMessages ? (
            <motion.div 
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              className="text-center py-8"
            >
              <div className="text-gray-400 mb-2">
                <Icon className="h-12 w-12 mx-auto opacity-30" />
              </div>
              <p className="text-sm text-gray-500 italic">
                Waiting for analysis...
              </p>
            </motion.div>
          ) : (
            agentMessages.map((message, index) => (
              <motion.div
                key={message.id || index}
                initial={{ opacity: 0, y: 20, scale: 0.95 }}
                animate={{ opacity: 1, y: 0, scale: 1 }}
                transition={{ delay: index * 0.1 }}
                className="bg-white/80 backdrop-blur-sm rounded-lg p-4 shadow-sm border border-white/50"
              >
                <div className="flex justify-between items-center mb-2">
                  <span className={`text-xs font-bold ${config.colors.primary} bg-white px-2 py-1 rounded-full`}>
                    Round {message.data.round}
                  </span>
                  <span className="text-xs text-gray-500">
                    {new Date(message.data.timestamp).toLocaleTimeString([], {
                      hour: '2-digit', 
                      minute: '2-digit'
                    })}
                  </span>
                </div>
                <p className="text-sm text-gray-800 leading-relaxed">
                  {message.data.content}
                </p>
              </motion.div>
            ))
          )}
        </AnimatePresence>
      </div>

      {/* Round Indicator */}
      {hasMessages && (
        <div className="mt-4 pt-3 border-t border-white/50">
          <div className="flex justify-between items-center text-xs">
            <span className="text-gray-600">
              {agentMessages.length} response{agentMessages.length !== 1 ? 's' : ''}
            </span>
            <span className={`${config.colors.primary} font-medium`}>
              Current: Round {currentRound}
            </span>
          </div>
        </div>
      )}
    </motion.div>
 
  );
};


export default AgentPanel;
