    }
  };
  return configs[agentType] || configs.innovator;
};

const ChatMessage = ({ message, isTyping = false }) => {
  const isJudge = message.type === 'judge_decision';
  const agentType = isJudge ? 'judge' : message.data?.role;
  const config = getAgentConfig(agentType);
  
  if (isJudge) {
    return (
      <motion.div
        initial={{ opacity: 0, y: 20, scale: 0.95 }}
        animate={{ opacity: 1, y: 0, scale: 1 }}
        transition={{ duration: 0.5, type: "spring" }}
        className="flex justify-center mb-8"
      >
        <div className="max-w-4xl w-full">
          <div className={`${config.colors.bg} backdrop-blur-sm rounded-3xl p-6 border-2 ${config.colors.border} relative overflow-hidden`}>
            {/* Judge glow effect */}
            <div className="absolute inset-0 bg-gradient-to-r from-amber-500/10 via-yellow-500/10 to-orange-500/10 animate-pulse" />
            
            <div className="relative z-10">
              <div className="flex items-center justify-between mb-4">
                <div className="flex items-center space-x-3">
                  <motion.div
                    className="text-3xl"
                    animate={{ scale: [1, 1.1, 1] }}
                    transition={{ duration: 2, repeat: Infinity }}
                  >
                    {config.avatar}
                  </motion.div>
                  <div>
                    <h4 className={`font-black text-lg ${config.colors.primary}`}>
                      {config.name}
                    </h4>
                    <p className="text-amber-300 text-sm font-medium">{config.title}</p>
                  </div>
                </div>
                
                <div className={`flex items-center space-x-3 px-4 py-2 rounded-xl font-bold text-sm border-2 ${
                  message.data.should_continue 
                    ? 'bg-green-500/20 text-green-300 border-green-500/40' 
                    : 'bg-red-500/20 text-red-300 border-red-500/40'
                }`}>
                  {message.data.should_continue ? (
                    <>
                      <CheckCircleIcon className="h-5 w-5" />
                      <span>Continue Debate</span>
                    </>
                  ) : (
                    <>
                      <XCircleIcon className="h-5 w-5" />
                      <span>Conclude Debate</span>
                    </>
                  )}
                </div>
              </div>
              
              <div className="bg-slate-900/50 backdrop-blur-sm rounded-2xl p-5 border border-slate-600/30">
                <div className="flex items-start space-x-3">
                  <div className="w-1 h-20 bg-gradient-to-b from-amber-400 to-yellow-400 rounded-full" />
                  <div className="flex-1">
                    <p className="text-amber-400 font-bold mb-2">Judicial Analysis:</p>
                    <p className="text-slate-200 leading-relaxed font-medium">
                      {message.data.reasoning}
                    </p>
                  </div>
                </div>
              </div>
              
              <div className="mt-4 text-center">
                <span className="text-amber-300 text-sm font-medium bg-amber-500/20 px-3 py-1 rounded-full">
                  Post-Round {message.data.round_count} Decision
                </span>
              </div>
            </div>
          </div>
        </div>
      </motion.div>
    );
  }

  return (
    <motion.div
      initial={{ opacity: 0, y: 20, x: agentType === 'innovator' || agentType === 'engineer' ? -30 : 30 }}
      animate={{ opacity: 1, y: 0, x: 0 }}
      transition={{ duration: 0.6, type: "spring", stiffness: 100 }}
      className={`flex ${agentType === 'innovator' || agentType === 'engineer' ? 'justify-start' : 'justify-end'} mb-6`}
    >
      <div className={`max-w-3xl ${agentType === 'innovator' || agentType === 'engineer' ? 'mr-12' : 'ml-12'}`}>
        <div className={`${config.colors.bg} backdrop-blur-sm rounded-2xl p-6 border-2 ${config.colors.border} relative overflow-hidden group`}>
          {/* Message glow effect */}
          <div className="absolute inset-0 bg-gradient-to-br from-white/5 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-500" />
          
          <div className="relative z-10">
            {/* Agent Header */}
            <div className={`flex items-center space-x-3 mb-4 ${agentType === 'skeptic' || agentType === 'ethicist' ? 'flex-row-reverse space-x-reverse' : ''}`}>
              <motion.div
                className="text-2xl"
                animate={isTyping ? { scale: [1, 1.2, 1] } : {}}
                transition={{ duration: 0.8, repeat: isTyping ? Infinity : 0 }}
              >
                {config.avatar}
              </motion.div>
              <div className={`${agentType === 'skeptic' || agentType === 'ethicist' ? 'text-right' : ''}`}>
                <h4 className={`font-black text-lg ${config.colors.primary}`}>
                  {config.name}
                </h4>
                <p className={`text-sm font-medium ${config.colors.primary.replace('400', '300')}`}>
                  {config.title}
                </p>
              </div>
              <div className="flex items-center space-x-2">
                <span className="text-xs text-slate-400 bg-slate-800/50 px-2 py-1 rounded-full">
                  Round {message.data?.round || 1}
                </span>
                <div className={`w-2 h-2 ${config.colors.primary.replace('text-', 'bg-')} rounded-full animate-pulse`} />
              </div>
            </div>
            
            {/* Message Content */}
            <div className="bg-slate-900/40 backdrop-blur-sm rounded-xl p-5 border border-slate-600/20">
              {isTyping ? (
                <div className="space-y-2">
                  <div className="flex space-x-1">
                    {[0, 1, 2].map(i => (
                      <motion.div
                        key={i}
                        className={`w-2 h-2 ${config.colors.primary.replace('text-', 'bg-')} rounded-full`}
                        animate={{ scale: [1, 1.5, 1], opacity: [0.5, 1, 0.5] }}
                        transition={{
                          duration: 1,
                          repeat: Infinity,
                          delay: i * 0.2
                        }}
                      />
                    ))}
                  </div>
                  <p className="text-slate-400 text-sm italic">Formulating response...</p>
                </div>
              ) : (
                <p className="text-slate-200 leading-relaxed font-medium text-base">
                  {message.data?.content}
                </p>
              )}
            </div>
            
            {/* Timestamp */}
            <div className={`mt-3 ${agentType === 'skeptic' || agentType === 'ethicist' ? 'text-right' : 'text-left'}`}>
              <span className="text-xs text-slate-500">
                {message.data?.timestamp ? new Date(message.data.timestamp).toLocaleTimeString() : 'Now'}
              </span>
            </div>
          </div>
        </div>
      </div>
    </motion.div>
  );
};

const RoundSeparator = ({ round, isActive = false }) => (
  <motion.div
    initial={{ opacity: 0, scale: 0.8 }}
    animate={{ opacity: 1, scale: 1 }}
    className="flex justify-center my-12"
  >
    <div className={`relative ${isActive ? 'animate-pulse' : ''}`}>
      <div className={`bg-gradient-to-r ${isActive ? 'from-purple-500/30 to-pink-500/30' : 'from-slate-700/50 to-slate-600/50'} backdrop-blur-sm rounded-2xl px-8 py-4 border-2 ${isActive ? 'border-purple-500/50' : 'border-slate-600/50'}`}>
        <div className="flex items-center space-x-4">
          <motion.div
            className={`w-4 h-4 rounded-full ${isActive ? 'bg-purple-400' : 'bg-slate-400'}`}
            animate={isActive ? { scale: [1, 1.3, 1] } : {}}
            transition={{ duration: 1, repeat: Infinity }}
          />
          <span className={`font-black text-xl ${isActive ? 'text-purple-300' : 'text-slate-400'}`}>
            Round {round}
          </span>
          {isActive && (
            <motion.span
              className="text-purple-400 text-sm font-bold bg-purple-500/20 px-3 py-1 rounded-full"
              animate={{ opacity: [0.7, 1, 0.7] }}
              transition={{ duration: 2, repeat: Infinity }}
            >
              Live
            </motion.span>
          )}
        </div>
      </div>
      {isActive && (
        <motion.div
          className="absolute inset-0 bg-gradient-to-r from-purple-500/20 to-pink-500/20 rounded-2xl blur-xl"
          animate={{ 
            scale: [1, 1.1, 1],
            opacity: [0.5, 0.8, 0.5]
          }}
          transition={{ duration: 2, repeat: Infinity }}
        />
      )}
    </div>
  </motion.div>
);

const DebateInterface = () => {
  const [query, setQuery] = useState('');
  const [sessionId] = useState(() => `session_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`);
  const [debateState, setDebateState] = useState({
    status: 'idle',
    researchBrief: null,
    messages: [],
    currentRound: 0,
    activeAgents: new Set(),
    startTime: null,
    typingAgent: null
  });

  const messagesEndRef = useRef(null);
  const processedMessagesCount = useRef(0);

  const { isConnected, messages: wsMessages, sendMessage, clearMessages, connectionStatus } = useWebSocket(
    `ws://localhost:8000/debate/${sessionId}`
  );

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [debateState.messages]);

  // Process WebSocket messages - similar to your original implementation
  useEffect(() => {
    const newMessages = wsMessages.slice(processedMessagesCount.current);
    if (newMessages.length === 0) return;

    setDebateState(prev => {
      const newState = { 
        ...prev,
        messages: [...prev.messages],
      };

      newMessages.forEach(message => {
        const messageExists = newState.messages.some(m => m.id === message.id);
        if (messageExists) return;

        // Convert messages to chronological chat format
        switch (message.type) {
          case 'research_complete':
            newState.status = 'research_complete';
            newState.researchBrief = message.data.brief;
            break;
          
          case 'agent_response':
            newState.status = 'debating';
            newState.messages.push({
              ...message,
              id: message.id || Date.now() + Math.random(),
              timestamp: new Date().toISOString()
            });
            newState.currentRound = Math.max(newState.currentRound, message.data.round || 1);
            newState.typingAgent = null; // Clear typing when response arrives
            break;
          
          case 'judge_decision':
            newState.status = 'judge_deciding';
            newState.messages.push({
              type: 'judge_decision',
              data: message.data,
              id: message.id || Date.now() + Math.random(),
              timestamp: new Date().toISOString()
            });
            break;
          
          case 'final_report':
            newState.status = 'complete';
            // You can handle final report display here
            break;
          
          case 'debate_complete':
            newState.status = 'complete';
            newState.typingAgent = null;
            break;
          
          case 'error':
            console.error('Debate error:', message.message);
            newState.status = 'error';
            newState.typingAgent = null;
            break;
          
          default:
            if (message.type === 'round_start') {
              newState.currentRound = message.data.round;
              newState.status = 'debating';
              // Simulate typing for next agent (you can enhance this based on your backend)
              newState.typingAgent = ['innovator', 'skeptic', 'engineer', 'ethicist'][Math.floor(Math.random() * 4)];
            }
            break;
        }
      });
      
      return newState;
    });

    processedMessagesCount.current = wsMessages.length;
  }, [wsMessages]);

  const startDebate = () => {
    if (!query.trim() || !isConnected) return;
    
    clearMessages();
    processedMessagesCount.current = 0;
    setDebateState({
      status: 'starting',
      researchBrief: null,
      messages: [],
      currentRound: 0,
      activeAgents: new Set(['researcher']),
      startTime: new Date(),
      typingAgent: null
    });

    const success = sendMessage({
      type: 'start_debate',
      query: query
    });

    if (success) {
      setDebateState(prev => ({ ...prev, status: 'researching' }));
    }
  };

  const resetDebate = () => {
    setQuery('');
    clearMessages();
    processedMessagesCount.current = 0;
    setDebateState({
      status: 'idle',
      researchBrief: null,
      messages: [],
      currentRound: 0,
      activeAgents: new Set(),
      startTime: null,
      typingAgent: null
    });
  };

  const groupMessagesByRound = (messages) => {
    const rounds = {};
    messages.forEach(msg => {
      const round = msg.data?.round || (msg.type === 'judge_decision' ? msg.data?.round_count : 1);
      if (!rounds[round]) {
        rounds[round] = [];
      }
      rounds[round].push(msg);
    });
    return rounds;
  };

  const roundGroups = groupMessagesByRound(debateState.messages);
  const sortedRounds = Object.keys(roundGroups).sort((a, b) => parseInt(a) - parseInt(b));
  const isDebateActive = ['starting', 'researching', 'research_complete', 'debating', 'judge_deciding'].includes(debateState.status);

  // Get connection status styling
  const getConnectionStatusConfig = () => {
    const configs = {
      connected: { 
        color: 'bg-emerald-500', 
        text: 'Connected', 
        textColor: 'text-emerald-400' 
      },
      connecting: { 
        color: 'bg-amber-500', 
        text: 'Connecting...', 
        textColor: 'text-amber-400' 
      },
      reconnecting: { 
        color: 'bg-amber-500', 
        text: 'Reconnecting...', 
        textColor: 'text-amber-400' 
      },
      disconnected: { 
        color: 'bg-red-500', 
        text: 'Disconnected', 
        textColor: 'text-red-400' 
      },
      error: { 
        color: 'bg-red-500', 
        text: 'Connection Error', 
        textColor: 'text-red-400' 
      }
    };
    return configs[connectionStatus] || configs.disconnected;
  };

  const connectionConfig = getConnectionStatusConfig();

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 relative overflow-hidden">
      {/* Animated Background */}
      <div className="absolute inset-0 overflow-hidden">
        <div className="absolute -top-40 -right-40 w-80 h-80 bg-purple-500 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-blob"></div>
        <div className="absolute -bottom-40 -left-40 w-80 h-80 bg-cyan-500 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-blob animation-delay-2000"></div>
        <div className="absolute top-40 left-1/2 w-80 h-80 bg-pink-500 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-blob animation-delay-4000"></div>
        <div className="absolute inset-0" style={{
          backgroundImage: `radial-gradient(circle at 1px 1px, rgba(99, 102, 241, 0.1) 1px, transparent 0)`,
          backgroundSize: '20px 20px'
        }}></div>
      </div>

      <div className="relative z-10 max-w-6xl mx-auto p-6">
        {/* Header */}
        <motion.div 
          initial={{ opacity: 0, y: -30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          className="text-center py-8 mb-8"
        >
          <h1 className="text-6xl md:text-7xl font-black bg-gradient-to-r from-cyan-400 via-purple-400 to-pink-400 bg-clip-text text-transparent mb-4 tracking-tight">
            Chimera
          </h1>
          <p className="text-xl text-slate-300 font-bold">Live Neural Debate Arena</p>
          <div className="flex justify-center items-center space-x-6 mt-4">
            <div className="flex items-center space-x-2 text-cyan-400">
              <SparklesIcon className="h-5 w-5" />
              <span className="text-sm font-medium">AI-Powered</span>
            </div>
            <div className="flex items-center space-x-2 text-purple-400">
              <BoltIcon className="h-5 w-5" />
              <span className="text-sm font-medium">Real-Time</span>
            </div>
          </div>
        </motion.div>

        {/* Query Input */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="bg-gradient-to-r from-slate-800/80 to-slate-900/80 backdrop-blur-xl rounded-3xl p-6 mb-8 border border-slate-700/50"
        >
          <div className="flex flex-col lg:flex-row gap-4">
            <div className="flex-1 relative group">
              <input
                type="text"
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                placeholder="Enter your debate topic..."
                className="w-full p-4 pl-12 bg-slate-900/50 border-2 border-slate-600/50 rounded-2xl focus:ring-4 focus:ring-purple-500/30 focus:border-purple-500/50 transition-all duration-300 text-white placeholder-slate-400 text-lg backdrop-blur-sm"
                disabled={isDebateActive}
                onKeyPress={(e) => e.key === 'Enter' && !isDebateActive && startDebate()}
              />
              <UserIcon className="absolute left-4 top-1/2 transform -translate-y-1/2 text-slate-400 h-5 w-5" />
            </div>
            
            <div className="flex space-x-3">
              <motion.button
                whileHover={{ scale: 1.02 }}
                whileTap={{ scale: 0.98 }}
                onClick={startDebate}
                disabled={!query.trim() || !isConnected || isDebateActive}
                className="px-8 py-4 bg-gradient-to-r from-purple-600 to-pink-600 text-white rounded-2xl hover:from-purple-700 hover:to-pink-700 disabled:from-slate-600 disabled:to-slate-700 disabled:cursor-not-allowed transition-all duration-300 flex items-center space-x-3 font-bold shadow-xl"
              >
                {isDebateActive ? (
                  <>
                    <PauseIcon className="h-5 w-5" />
                    <span>Active</span>
                  </>
                ) : (
                  <>
                    <PlayIcon className="h-5 w-5" />
                    <span>Start Debate</span>
                  </>
                )}
              </motion.button>
              
              <motion.button
                whileHover={{ scale: 1.02 }}
                whileTap={{ scale: 0.98 }}
                onClick={resetDebate}
                disabled={isDebateActive}
                className="px-6 py-4 bg-slate-700 text-slate-300 rounded-2xl hover:bg-slate-600 disabled:opacity-50 disabled:cursor-not-allowed transition-all duration-300 flex items-center space-x-2 font-semibold"
              >
                <ArrowPathIcon className="h-5 w-5" />
                <span>Reset</span>
              </motion.button>
            </div>
          </div>
        </motion.div>

        {/* Show debate topic only if there's an active query */}
        {query && (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            className="bg-gradient-to-r from-slate-800/80 to-slate-900/80 backdrop-blur-xl rounded-3xl p-6 mb-8 border border-slate-700/50"
          >
            <div className="flex items-center space-x-4 mb-4">
              <div className="bg-purple-500/20 p-3 rounded-2xl">
                <DocumentTextIcon className="h-6 w-6 text-purple-400" />
              </div>
              <div>
                <h3 className="text-xl font-bold text-white">Debate Topic</h3>
                <p className="text-slate-400 text-sm">Neural combat initiated</p>
              </div>
            </div>
            <p className="text-lg text-slate-200 font-medium leading-relaxed">{query}</p>
          </motion.div>
        )}

        {/* Participants Bar - only show when debate is active */}
        {debateState.messages.length > 0 && (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            className="bg-slate-800/50 backdrop-blur-sm rounded-2xl p-4 mb-8 border border-slate-700/50"
          >
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-6">
                <span className="text-slate-400 font-medium">Active Neural Agents:</span>
                {['innovator', 'skeptic', 'engineer', 'ethicist'].map(agent => {
                  const config = getAgentConfig(agent);
                  const isActive = debateState.typingAgent === agent;
                  return (
                    <motion.div
                      key={agent}
                      className={`flex items-center space-x-2 px-3 py-2 rounded-xl border-2 ${config.colors.border} ${config.colors.accent} backdrop-blur-sm`}
                      animate={isActive ? { scale: [1, 1.05, 1] } : {}}
                      transition={{ duration: 1, repeat: Infinity }}
                    >
                      <span className="text-lg">{config.avatar}</span>
                      <span className={`text-sm font-bold ${config.colors.primary}`}>
                        {config.name}
                      </span>
                      {isActive && (
                        <motion.div
                          className={`w-2 h-2 ${config.colors.primary.replace('text-', 'bg-')} rounded-full`}
                          animate={{ scale: [1, 1.5, 1] }}
                          transition={{ duration: 0.8, repeat: Infinity }}
                        />
                      )}
                    </motion.div>
                  );
                })}
              </div>
              <div className="flex items-center space-x-3">
                <span className="text-slate-400 text-sm">Round</span>
                <span className="bg-purple-500/20 text-purple-400 font-bold px-3 py-1 rounded-full">
                  {debateState.currentRound}
                </span>
              </div>
            </div>
          </motion.div>
        )}

        {/* Chat Container - only show when there are messages */}
        {debateState.messages.length > 0 && (
          <div className="bg-slate-800/30 backdrop-blur-sm rounded-3xl border border-slate-700/50 overflow-hidden mb-8">
            <div className="h-[800px] overflow-y-auto p-6 space-y-2 custom-scrollbar">
              {sortedRounds.map(round => (
                <div key={round}>
                  <RoundSeparator 
                    round={parseInt(round)} 
                    isActive={parseInt(round) === debateState.currentRound}
                  />
                  <AnimatePresence>
                    {roundGroups[round].map(message => (
                      <ChatMessage key={message.id} message={message} />
                    ))}
                  </AnimatePresence>
                </div>
              ))}
              
              {/* Show typing indicator */}
              {debateState.typingAgent && (
                <ChatMessage 
                  message={{
                    type: 'agent_response',
                    data: { role: debateState.typingAgent, round: debateState.currentRound }
                  }}
                  isTyping={true}
                />
              )}
              
              <div ref={messagesEndRef} />
            </div>
          </div>
        )}

        {/* Status Bar */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="flex justify-between items-center"
        >
          <div className="bg-gradient-to-r from-purple-500/20 to-pink-500/20 backdrop-blur-sm border border-purple-500/30 rounded-2xl px-6 py-3">
            <p className={`${connectionConfig.textColor} font-bold flex items-center`}>
              <motion.div
                className={`w-2 h-2 ${connectionConfig.color} rounded-full mr-3`}
                animate={{ scale: [1, 1.5, 1] }}
                transition={{ duration: 1, repeat: Infinity }}
              />
              {connectionConfig.text}
            </p>
          </div>
          
          <div className="bg-slate-800/50 backdrop-blur-sm border border-slate-700/50 rounded-2xl px-6 py-3">
            <div className="flex items-center space-x-4 text-sm text-slate-400">
              <span>Messages: <span className="text-white font-bold">{debateState.messages.length}</span></span>
              <span>Round: <span className="text-purple-400 font-bold">{debateState.currentRound}</span></span>
              <span>Status: <span className={`font-bold ${debateState.status === 'complete' ? 'text-green-400' : isDebateActive ? 'text-yellow-400' : 'text-slate-400'}`}>
                {debateState.status === 'idle' ? 'Ready' : 
                 debateState.status === 'complete' ? 'Complete' : 
                 isDebateActive ? 'Active' : debateState.status}
              </span></span>
            </div>
          </div>
        </motion.div>
      </div>
    </div>
  );
};

export default DebateInterface;
