import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  LightBulbIcon, 
  ShieldExclamationIcon, 
  CogIcon, 
  ScaleIcon,
  ChevronDownIcon,
  ChevronUpIcon
} from '@heroicons/react/24/outline';

const getAgentConfig = (agentType) => {
  const configs = {
    innovator: {
      icon: LightBulbIcon,
      title: 'The Innovator',
      description: 'Champions progress & breakthrough solutions',
      colors: {
        primary: 'text-emerald-700',
        bg: 'bg-gradient-to-br from-emerald-50 to-green-50',
        border: 'border-emerald-200',
        accent: 'bg-emerald-100',
        glow: 'shadow-emerald-200/50'
      }
    },
    skeptic: {
      icon: ShieldExclamationIcon,
      title: 'The Skeptic',
      description: 'Challenges assumptions & identifies risks',
      colors: {
        primary: 'text-red-700',
        bg: 'bg-gradient-to-br from-red-50 to-pink-50',
        border: 'border-red-200',
        accent: 'bg-red-100',
        glow: 'shadow-red-200/50'
      }
    },
    engineer: {
      icon: CogIcon,
      title: 'The Engineer',
      description: 'Focuses on feasibility & implementation',
      colors: {
        primary: 'text-blue-700',
        bg: 'bg-gradient-to-br from-blue-50 to-cyan-50',
        border: 'border-blue-200',
        accent: 'bg-blue-100',
        glow: 'shadow-blue-200/50'
      }
    },
    ethicist: {
      icon: ScaleIcon,
      title: 'The Ethicist',
      description: 'Examines moral implications & social impact',
      colors: {
        primary: 'text-purple-700',
        bg: 'bg-gradient-to-br from-purple-50 to-indigo-50',
        border: 'border-purple-200',
        accent: 'bg-purple-100',
        glow: 'shadow-purple-200/50'
      }
    }
  };
  return configs[agentType] || configs.innovator;
};

const AgentPanel = ({ agent, messages = [], currentRound = 1, isActive = false, debateStatus = 'idle' }) => {
  const [expandedRounds, setExpandedRounds] = useState(new Set([currentRound])); // Auto-expand current round
  const config = getAgentConfig(agent);
  const Icon = config.icon;
  
  // Filter and group messages by round
  const agentMessages = messages
    .filter(msg => msg.data && msg.data.agent === agent)
    .sort((a, b) => (a.data.round || 0) - (b.data.round || 0));

  // Group messages by round
  const messagesByRound = agentMessages.reduce((acc, msg) => {
    const round = msg.data.round || 1;
    if (!acc[round]) acc[round] = [];
    acc[round].push(msg);
    return acc;
  }, {});

  const hasMessages = agentMessages.length > 0;
  const hasMessageForCurrentRound = messagesByRound[currentRound]?.length > 0;
  
  // Determine if we should show "waiting" state
  const shouldShowWaiting = () => {
    if (debateStatus === 'debating' && currentRound > 0) {
      return !hasMessageForCurrentRound && !isActive;
    }
    return false;
  };

  const toggleRoundExpansion = (round) => {
    const newExpanded = new Set(expandedRounds);
    if (newExpanded.has(round)) {
      newExpanded.delete(round);
    } else {
      newExpanded.add(round);
    }
    setExpandedRounds(newExpanded);
  };

  // Get all rounds that have messages or current active round
  const allRounds = [...new Set([
    ...Object.keys(messagesByRound).map(Number),
    ...(currentRound > 0 ? [currentRound] : [])
  ])].sort((a, b) => b - a); // Reverse order (latest first)

  return (
    <motion.div 
      initial={{ opacity: 0, scale: 0.95 }}
      animate={{ opacity: 1, scale: 1 }}
      className={`
        ${config.colors.bg} 
        ${config.colors.border} 
        border-2 rounded-xl p-5 h-full min-h-[500px] max-h-[700px]
        ${isActive ? `${config.colors.glow} shadow-xl scale-[1.02]` : 'shadow-lg'}
        transition-all duration-300 relative overflow-hidden flex flex-col
      `}
    >
      {/* Status Indicator */}
      {isActive && (
        <div className="absolute top-2 right-2 z-10">
          <div className={`${config.colors.accent} rounded-full px-2 py-1`}>
            <div className="flex items-center space-x-1">
              <div className="w-2 h-2 bg-current rounded-full animate-pulse"></div>
              <span className="text-xs font-medium">Thinking...</span>
            </div>
          </div>
        </div>
      )}

      {/* Header */}
      <div className="flex items-center space-x-3 mb-4 flex-shrink-0">
        <div className={`${config.colors.accent} p-2 rounded-lg`}>
          <Icon className={`h-6 w-6 ${config.colors.primary}`} />
        </div>
        <div className="flex-1 min-w-0">
          <h3 className={`font-bold text-lg ${config.colors.primary} truncate`}>
            {config.title}
          </h3>
          <p className="text-xs text-gray-600 leading-tight">
            {config.description}
          </p>
        </div>
      </div>
      
      {/* Messages by Round */}
      <div className="flex-1 overflow-y-auto scrollbar-thin scrollbar-thumb-gray-300 space-y-3">
        <AnimatePresence>
          {!hasMessages && !shouldShowWaiting() ? (
            <motion.div 
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              className="text-center py-8"
            >
              <div className="text-gray-400 mb-2">
                <Icon className="h-12 w-12 mx-auto opacity-30" />
              </div>
              <p className="text-sm text-gray-500 italic">
                Ready for analysis...
              </p>
            </motion.div>
          ) : shouldShowWaiting() ? (
            <motion.div 
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              className="text-center py-8"
            >
              <div className="text-gray-400 mb-2">
                <Icon className="h-12 w-12 mx-auto opacity-30 animate-pulse" />
              </div>
              <p className="text-sm text-gray-500 italic">
                Analyzing for Round {currentRound}...
              </p>
            </motion.div>
          ) : (
            <>
              {allRounds.map(round => (
                <RoundSection
                  key={round}
                  round={round}
                  messages={messagesByRound[round] || []}
                  isExpanded={expandedRounds.has(round)}
                  onToggleExpansion={toggleRoundExpansion}
                  config={config}
                  isCurrentRound={round === currentRound}
                  isWaitingForRound={round === currentRound && !messagesByRound[round] && debateStatus === 'debating'}
                />
              ))}
            </>
          )}
        </AnimatePresence>
      </div>

      {/* Footer Stats */}
      <div className="mt-4 pt-3 border-t border-white/50 flex-shrink-0">
        <div className="flex justify-between items-center text-xs">
          <span className="text-gray-600">
            {agentMessages.length} response{agentMessages.length !== 1 ? 's' : ''} â€¢ {allRounds.length} round{allRounds.length !== 1 ? 's' : ''}
          </span>
          <span className={`${config.colors.primary} font-medium`}>
            Current: Round {currentRound}
          </span>
        </div>
      </div>
    </motion.div>
  );
};

const RoundSection = ({ round, messages, isExpanded, onToggleExpansion, config, isCurrentRound, isWaitingForRound }) => {
  const hasMessages = messages && messages.length > 0;
  
  return (
    <div className="border border-white/30 rounded-lg overflow-hidden">
      {/* Round Header */}
      <button
        onClick={() => onToggleExpansion(round)}
        className={`w-full px-3 py-2 flex items-center justify-between transition-all duration-200 ${
          isCurrentRound 
            ? `${config.colors.accent} ${config.colors.primary} font-semibold` 
            : 'bg-white/50 text-gray-700 hover:bg-white/70'
        }`}
      >
        <div className="flex items-center space-x-2">
          <span className="text-sm font-medium">Round {round}</span>
          {isCurrentRound && (
            <span className="text-xs bg-white/80 px-2 py-0.5 rounded-full">
              Current
            </span>
          )}
          {isWaitingForRound && (
            <span className="text-xs text-orange-600 animate-pulse">
              Analyzing...
            </span>
          )}
        </div>
        
        <div className="flex items-center space-x-2">
          {hasMessages && (
            <span className="text-xs opacity-70">
              {messages.length} response{messages.length !== 1 ? 's' : ''}
            </span>
          )}
          {isExpanded ? (
            <ChevronUpIcon className="h-4 w-4" />
          ) : (
            <ChevronDownIcon className="h-4 w-4" />
          )}
        </div>
      </button>

      {/* Round Content */}
      <AnimatePresence>
        {isExpanded && (
          <motion.div
            initial={{ height: 0, opacity: 0 }}
            animate={{ height: 'auto', opacity: 1 }}
            exit={{ height: 0, opacity: 0 }}
            transition={{ duration: 0.2 }}
            className="overflow-hidden"
          >
            <div className="p-3 space-y-3 bg-white/20">
              {isWaitingForRound ? (
                <div className="text-center py-4">
                  <div className="w-6 h-6 border-2 border-current border-t-transparent rounded-full animate-spin mx-auto mb-2 opacity-50"></div>
                  <p className="text-xs text-gray-600 italic">Waiting for analysis...</p>
                </div>
              ) : !hasMessages ? (
                <div className="text-center py-4">
                  <p className="text-xs text-gray-500 italic">No response for this round</p>
                </div>
              ) : (
                messages.map((message, index) => (
                  <motion.div
                    key={message.id || `${round}-${index}`}
                    initial={{ opacity: 0, y: 10 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: index * 0.05 }}
                    className="bg-white/60 backdrop-blur-sm rounded-lg p-3 border border-white/50"
                  >
                    <div className="flex justify-between items-center mb-2">
                      <span className="text-xs text-gray-500">
                        {new Date(message.data.timestamp).toLocaleTimeString([], {
                          hour: '2-digit', 
                          minute: '2-digit'
                        })}
                      </span>
                      {message.data.round && (
                        <span className="text-xs font-medium text-gray-600 bg-white/50 px-2 py-0.5 rounded-full">
                          R{message.data.round}
                        </span>
                      )}
                    </div>
                    <p className="text-sm text-gray-800 leading-relaxed">
                      {message.data.content}
                    </p>
                  </motion.div>
                ))
              )}
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default AgentPanel;
