import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  ArrowPathIcon, 
  PlayIcon,
  PauseIcon,
  SparklesIcon,
  BoltIcon,
  RocketLaunchIcon
} from '@heroicons/react/24/outline';

import useWebSocket from '../hooks/useWebSocket';
import ResearchPanel from './ResearchPanel';
import AgentPanel from './AgentPanel';
import JudgePanel from './JudgePanel';
import FinalReport from './FinalReport';

const DebateInterface = () => {
  const [query, setQuery] = useState('');
  const [sessionId] = useState(() => `session_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`);
  const [debateState, setDebateState] = useState({
    status: 'idle',
    researchBrief: null,
    agentMessages: [],
    judgeDecisions: [],
    finalReport: null,
    currentRound: 0,
    startTime: null
  });

  const messagesEndRef = useRef(null);
  const { isConnected, messages, sendMessage, clearMessages, connectionStatus } = useWebSocket(
    `ws://localhost:8000/debate/${sessionId}`
  );

  const processedMessagesCount = useRef(0);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [debateState.agentMessages, debateState.finalReport]);

  // Correct, robust message processing logic
  useEffect(() => {
    const newMessages = messages.slice(processedMessagesCount.current);
    if (newMessages.length === 0) return;

    setDebateState(prev => {
      const newState = { ...prev };
      newMessages.forEach(message => {
        switch (message.type) {
          case 'research_complete':
            newState.status = 'research_complete';
            newState.researchBrief = message.data.brief;
            break;
          case 'agent_response':
            newState.status = 'debating';
            if (!newState.agentMessages.some(m => m.id === message.id)) {
              newState.agentMessages.push(message);
            }
            newState.currentRound = Math.max(newState.currentRound, message.data.round || 1);
            break;
          case 'judge_decision':
            newState.status = 'judge_deciding';
            if (!newState.judgeDecisions.some(d => d.id === message.id)) {
                newState.judgeDecisions.push(message);
            }
            break;
          case 'final_report':
            newState.status = 'complete';
            newState.finalReport = message.data;
            break;
          case 'debate_complete':
            newState.status = 'complete';
            break;
          case 'error':
            console.error('Debate error:', message.message);
            newState.status = 'error';
            break;
          default:
            break;
        }
      });
      return newState;
    });

    processedMessagesCount.current = messages.length;
  }, [messages]);

  const startDebate = () => {
    if (!query.trim() || !isConnected) return;
    
    clearMessages();
    processedMessagesCount.current = 0;
    setDebateState({
      status: 'starting',
      researchBrief: null,
      agentMessages: [],
      judgeDecisions: [],
      finalReport: null,
      currentRound: 0,
      startTime: new Date()
    });

    const success = sendMessage({
      type: 'start_debate',
      query: query
    });

    if (success) {
      setDebateState(prev => ({ ...prev, status: 'researching' }));
    }
  };

  const resetDebate = () => {
    setQuery('');
    clearMessages();
    processedMessagesCount.current = 0;
    setDebateState({
      status: 'idle',
      researchBrief: null,
      agentMessages: [],
      judgeDecisions: [],
      finalReport: null,
      currentRound: 0,
      startTime: null
    });
  };

  const getStatusConfig = () => {
    const configs = {
      idle: { 
        message: 'Ready to unleash the power of AI debate', 
        color: 'text-slate-300',
        bgColor: 'bg-slate-800/50',
        borderColor: 'border-slate-600/50',
        icon: 'ðŸ§ ',
        glow: 'shadow-slate-500/10'
      },
      starting: { 
        message: 'Initializing neural debate matrix...', 
        color: 'text-cyan-300',
        bgColor: 'bg-cyan-900/40',
        borderColor: 'border-cyan-500/30',
        icon: 'âš¡',
        glow: 'shadow-cyan-500/30'
      },
      researching: { 
        message: 'AI researcher scanning global knowledge...', 
        color: 'text-blue-300',
        bgColor: 'bg-blue-900/40',
        borderColor: 'border-blue-500/30',
        icon: 'ðŸ”',
        glow: 'shadow-blue-500/30'
      },
      research_complete: { 
        message: 'Knowledge acquired. Experts awakening...', 
        color: 'text-emerald-300',
        bgColor: 'bg-emerald-900/40',
        borderColor: 'border-emerald-500/30',
        icon: 'âœ¨',
        glow: 'shadow-emerald-500/30'
      },
      debating: { 
        message: `Round ${debateState.currentRound} â€¢ Neural combat engaged`, 
        color: 'text-purple-300',
        bgColor: 'bg-purple-900/40',
        borderColor: 'border-purple-500/30',
        icon: 'ðŸŽ¯',
        glow: 'shadow-purple-500/30'
      },
      judge_deciding: { 
        message: 'Supreme Judge evaluating neural patterns...', 
        color: 'text-amber-300',
        bgColor: 'bg-amber-900/40',
        borderColor: 'border-amber-500/30',
        icon: 'âš–ï¸',
        glow: 'shadow-amber-500/30'
      },
      moderating: { 
        message: 'Synthesizing collective intelligence...', 
        color: 'text-indigo-300',
        bgColor: 'bg-indigo-900/40',
        borderColor: 'border-indigo-500/30',
        icon: 'ðŸ§¬',
        glow: 'shadow-indigo-500/30'
      },
      complete: { 
        message: 'Debate matrix complete â€¢ Insights extracted', 
        color: 'text-emerald-300',
        bgColor: 'bg-emerald-900/40',
        borderColor: 'border-emerald-500/30',
        icon: 'ðŸŽ‰',
        glow: 'shadow-emerald-500/30'
      },
      error: { 
        message: 'Neural network disruption detected', 
        color: 'text-red-300',
        bgColor: 'bg-red-900/40',
        borderColor: 'border-red-500/30',
        icon: 'âš ï¸',
        glow: 'shadow-red-500/30'
      }
    };
    return configs[debateState.status] || configs.idle;
  };

  const isDebateActive = ['starting', 'researching', 'research_complete', 'debating', 'judge_deciding', 'moderating'].includes(debateState.status);
  const statusConfig = getStatusConfig();

  const getConnectionStatusConfig = () => {
    const configs = {
      connected: { 
        color: 'bg-emerald-500', 
        text: 'Neural Link Active', 
        textColor: 'text-emerald-300',
        bgColor: 'bg-emerald-900/40',
        borderColor: 'border-emerald-500/30' 
      },
      connecting: { 
        color: 'bg-amber-500', 
        text: 'Establishing Link...', 
        textColor: 'text-amber-300',
        bgColor: 'bg-amber-900/40',
        borderColor: 'border-amber-500/30' 
      },
      reconnecting: { 
        color: 'bg-amber-500', 
        text: 'Reconnecting...', 
        textColor: 'text-amber-300',
        bgColor: 'bg-amber-900/40',
        borderColor: 'border-amber-500/30' 
      },
      disconnected: { 
        color: 'bg-red-500', 
        text: 'Neural Link Offline', 
        textColor: 'text-red-300',
        bgColor: 'bg-red-900/40',
        borderColor: 'border-red-500/30' 
      },
      error: { 
        color: 'bg-red-500', 
        text: 'Connection Error', 
        textColor: 'text-red-300',
        bgColor: 'bg-red-900/40',
        borderColor: 'border-red-500/30' 
      },
      failed: { 
        color: 'bg-red-500', 
        text: 'Link Failed', 
        textColor: 'text-red-300',
        bgColor: 'bg-red-900/40',
        borderColor: 'border-red-500/30' 
      }
    };
    return configs[connectionStatus] || configs.disconnected;
  };

  const connectionConfig = getConnectionStatusConfig();

  return (
    <div className="min-h-screen bg-slate-900 text-slate-200 relative overflow-hidden">
      <div className="absolute inset-0 bg-grid-slate-700/10 [mask-image:linear-gradient(to_bottom,white_20%,transparent_75%)]"></div>
      <div className="absolute inset-0 pointer-events-none [mask-image:radial-gradient(50%_50%_at_50%_50%,black_40%,transparent_100%)]">
        <div className="absolute inset-0 bg-gradient-to-br from-purple-500/20 via-transparent to-cyan-500/20 opacity-50 animate-blob"></div>
      </div>

      <div className="relative z-10 max-w-7xl mx-auto p-6 space-y-8">
        
        <motion.div 
          initial={{ opacity: 0, y: -30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, ease: "easeOut" }}
          className="text-center py-12"
        >
          <h1 className="text-7xl md:text-8xl font-black bg-gradient-to-r from-cyan-400 via-purple-400 to-pink-400 bg-clip-text text-transparent mb-4 tracking-tight">
            Chimera
          </h1>
          <p className="text-2xl md:text-3xl text-slate-300 font-bold tracking-wide">
            Neural Multi-Agent Debate System
          </p>
        </motion.div>

        <motion.div 
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.3, duration: 0.8 }}
          className="relative"
        >
          <div className="bg-slate-800/50 backdrop-blur-xl rounded-3xl shadow-2xl border border-slate-700/50 p-8">
            <div className="flex flex-col lg:flex-row gap-4">
              <div className="flex-1 relative group">
                <input
                  type="text"
                  value={query}
                  onChange={(e) => setQuery(e.target.value)}
                  placeholder="Enter your complex question or debate topic..."
                  className="w-full p-6 pl-16 bg-slate-900/50 border-2 border-slate-600/50 rounded-2xl focus:ring-4 focus:ring-purple-500/30 focus:border-purple-500/50 transition-all duration-300 text-white placeholder-slate-400 text-lg font-medium group-hover:border-slate-500/70"
                  disabled={isDebateActive}
                  onKeyPress={(e) => e.key === 'Enter' && !isDebateActive && startDebate()}
                />
                <div className="absolute left-6 top-1/2 transform -translate-y-1/2 text-slate-400 group-hover:text-cyan-400 transition-colors duration-300 text-2xl">
                  ðŸ§ 
                </div>
              </div>
              
              <div className="flex space-x-4">
                <motion.button
                  whileHover={{ scale: 1.02, boxShadow: "0 10px 30px -10px rgba(139, 92, 246, 0.4)" }}
                  whileTap={{ scale: 0.98 }}
                  onClick={startDebate}
                  disabled={!query.trim() || !isConnected || isDebateActive}
                  className="px-8 py-6 bg-gradient-to-r from-purple-600 to-pink-600 text-white rounded-2xl hover:from-purple-700 hover:to-pink-700 disabled:from-slate-600 disabled:to-slate-700 disabled:cursor-not-allowed transition-all duration-300 flex items-center space-x-3 font-bold text-lg shadow-xl border border-purple-500/50"
                >
                  {isDebateActive ? (
                    <>
                      <PauseIcon className="h-6 w-6" />
                      <span>In Progress</span>
                    </>
                  ) : (
                    <>
                      <PlayIcon className="h-6 w-6" />
                      <span>Ignite Debate</span>
                    </>
                  )}
                </motion.button>
                
                <motion.button
                  whileHover={{ scale: 1.02 }}
                  whileTap={{ scale: 0.98 }}
                  onClick={resetDebate}
                  disabled={isDebateActive}
                  className="px-6 py-6 bg-slate-700 text-slate-300 rounded-2xl hover:bg-slate-600 disabled:opacity-50 disabled:cursor-not-allowed transition-all duration-300 flex items-center space-x-3 font-semibold shadow-xl border border-slate-600/50"
                >
                  <ArrowPathIcon className="h-6 w-6" />
                </motion.button>
              </div>
            </div>
          </div>
        </motion.div>

        <motion.div 
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.6, duration: 0.6 }}
          className="flex flex-col sm:flex-row items-center justify-between gap-4"
        >
          <div className={`inline-flex items-center px-6 py-3 rounded-2xl font-bold text-sm border-2 backdrop-blur-sm ${statusConfig.bgColor} ${statusConfig.color} ${statusConfig.borderColor} ${statusConfig.glow} shadow-xl`}>
            <span className="mr-3 text-lg">{statusConfig.icon}</span>
            {statusConfig.message}
          </div>
          
          <div className={`inline-flex items-center px-4 py-2 rounded-xl text-sm font-bold border-2 backdrop-blur-sm ${connectionConfig.bgColor} ${connectionConfig.textColor} ${connectionConfig.borderColor} shadow-lg`}>
            <div className={`w-3 h-3 rounded-full mr-3 ${connectionConfig.color} ${connectionStatus === 'connecting' || connectionStatus === 'reconnecting' ? 'animate-pulse' : ''}`} />
            {connectionConfig.text}
          </div>
        </motion.div>

        <AnimatePresence>
          {(debateState.status === 'researching' || debateState.researchBrief) && (
            <ResearchPanel 
              researchBrief={debateState.researchBrief} 
              isLoading={debateState.status === 'researching'}
            />
          )}
        </AnimatePresence>

        <AnimatePresence>
          {debateState.judgeDecisions.length > 0 && (
            <JudgePanel judgeDecisions={debateState.judgeDecisions} />
          )}
        </AnimatePresence>

        <AnimatePresence>
          {(isDebateActive || debateState.agentMessages.length > 0) && (
            <motion.div 
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.2, duration: 0.8, staggerChildren: 0.1 }}
              className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-4 gap-6"
            >
              {['innovator', 'skeptic', 'engineer', 'ethicist'].map((agent) => (
                <AgentPanel
                  key={agent}
                  agent={agent}
                  messages={debateState.agentMessages}
                  currentRound={debateState.currentRound}
                  isActive={false}
                  debateStatus={debateState.status}
                />
              ))}
            </motion.div>
          )}
        </AnimatePresence>

        <AnimatePresence>
          {debateState.finalReport && (
            <FinalReport report={debateState.finalReport} />
          )}
        </AnimatePresence>

        <div ref={messagesEndRef} />

        {process.env.NODE_ENV === 'development' && (
          <motion.div 
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            className="bg-slate-900/80 backdrop-blur-xl text-cyan-400 p-6 rounded-2xl font-mono text-xs space-y-2 border border-slate-700/50 shadow-2xl"
          >
            <div className="text-cyan-300 font-bold mb-3">DEBUG CONSOLE</div>
            <div>Session: <span className="text-purple-400">{sessionId}</span></div>
            <div>Status: <span className="text-green-400">{debateState.status}</span></div>
            <div>WS Msgs: <span className="text-yellow-400">{messages.length}</span></div>
            <div>Processed: <span className="text-yellow-400">{processedMessagesCount.current}</span></div>
            <div>Round: <span className="text-pink-400">{debateState.currentRound}</span></div>
            <div>Connection: <span className="text-blue-400">{connectionStatus}</span></div>
            <div>Agent Msgs: <span className="text-indigo-400">{debateState.agentMessages.length}</span></div>
            {debateState.startTime && (
              <div>Duration: <span className="text-emerald-400">{Math.round((new Date() - debateState.startTime) / 1000)}s</span></div>
            )}
          </motion.div>
        )}
      </div>
    </div>
  );
};

export default DebateInterface;
