import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  LightBulbIcon, 
  ShieldExclamationIcon, 
  CogIcon, 
  ScaleIcon,
  ChevronDownIcon,
  UserIcon
} from '@heroicons/react/24/outline';

const getAgentConfig = (agentType) => {
  const configs = {
    innovator: {
      icon: LightBulbIcon,
      title: 'Alex Chen',
      subtitle: 'The Innovator',
      description: 'Visionary strategist pushing breakthrough solutions',
      personality: 'ðŸš€ Optimistic â€¢ Forward-thinking â€¢ Disruptive',
      colors: {
        primary: 'text-emerald-400',
        bg: 'bg-gradient-to-br from-emerald-900/40 via-green-900/40 to-teal-900/40',
        border: 'border-emerald-500/30',
        accent: 'bg-emerald-500/20',
        glow: 'shadow-emerald-500/30',
        particle: 'bg-emerald-400'
      }
    },
    skeptic: {
      icon: ShieldExclamationIcon,
      title: 'Dr. Sarah Reeves',
      subtitle: 'The Skeptic',
      description: 'Risk analyst cutting through hype with precision',
      personality: 'ðŸ” Analytical â€¢ Evidence-based â€¢ Critical',
      colors: {
        primary: 'text-red-400',
        bg: 'bg-gradient-to-br from-red-900/40 via-pink-900/40 to-rose-900/40',
        border: 'border-red-500/30',
        accent: 'bg-red-500/20',
        glow: 'shadow-red-500/30',
        particle: 'bg-red-400'
      }
    },
    engineer: {
      icon: CogIcon,
      title: 'Marcus Torres',
      subtitle: 'The Engineer',
      description: 'Systems architect bridging vision with reality',
      personality: 'âš™ï¸ Pragmatic â€¢ Technical â€¢ Solution-focused',
      colors: {
        primary: 'text-blue-400',
        bg: 'bg-gradient-to-br from-blue-900/40 via-cyan-900/40 to-sky-900/40',
        border: 'border-blue-500/30',
        accent: 'bg-blue-500/20',
        glow: 'shadow-blue-500/30',
        particle: 'bg-blue-400'
      }
    },
    ethicist: {
      icon: ScaleIcon,
      title: 'Dr. Amara Okafor',
      subtitle: 'The Ethicist',
      description: 'Moral philosopher examining human impact',
      personality: 'âš–ï¸ Principled â€¢ Human-centered â€¢ Thoughtful',
      colors: {
        primary: 'text-purple-400',
        bg: 'bg-gradient-to-br from-purple-900/40 via-indigo-900/40 to-violet-900/40',
        border: 'border-purple-500/30',
        accent: 'bg-purple-500/20',
        glow: 'shadow-purple-500/30',
        particle: 'bg-purple-400'
      }
    }
  };
  return configs[agentType] || configs.innovator;
};

const AgentPanel = ({ agent, messages = [], currentRound = 1, isActive = false, debateStatus = 'idle' }) => {
  const [expandedRounds, setExpandedRounds] = useState(new Set());
  const config = getAgentConfig(agent);
  const Icon = config.icon;

  useEffect(() => {
    if (currentRound > 0) {
      setExpandedRounds(prev => new Set(prev).add(currentRound));
    }
  }, [currentRound]);
  
  const agentMessages = messages
    .filter(msg => msg.data && msg.data.role === agent) // <-- CRITICAL FIX: was msg.data.agent
    .sort((a, b) => (a.data.round || 0) - (b.data.round || 0));

  const messagesByRound = agentMessages.reduce((acc, msg) => {
    const round = msg.data.round || 1;
    if (!acc[round]) acc[round] = [];
    acc[round].push(msg);
    return acc;
  }, {});

  const roundsWithMessages = Object.keys(messagesByRound).map(Number);
  const allPossibleRounds = Array.from({ length: currentRound > 0 ? currentRound : 0 }, (_, i) => i + 1);
  const allRounds = [...new Set([...roundsWithMessages, ...allPossibleRounds])].sort((a, b) => b - a);

  const toggleRoundExpansion = (round) => {
    const newExpanded = new Set(expandedRounds);
    if (newExpanded.has(round)) {
      newExpanded.delete(round);
    } else {
      newExpanded.add(round);
    }
    setExpandedRounds(newExpanded);
  };

  return (
    <motion.div 
      className={`
        ${config.colors.bg} 
        backdrop-blur-xl border-2 ${config.colors.border} rounded-3xl p-6 h-full min-h-[600px] max-h-[800px]
        ${isActive ? `${config.colors.glow} shadow-2xl` : 'shadow-xl'}
        transition-all duration-500 relative overflow-hidden flex flex-col
      `}
    >
      <div className="flex items-start space-x-4 mb-6 flex-shrink-0 relative z-10">
        <div className={`${config.colors.accent} backdrop-blur-sm p-4 rounded-2xl border ${config.colors.border}`}>
          <Icon className={`h-8 w-8 ${config.colors.primary}`} />
        </div>
        <div className="flex-1 min-w-0">
          <h3 className={`text-2xl font-black ${config.colors.primary} mb-1`}>{config.title}</h3>
          <p className="text-slate-300 font-bold text-sm mb-2">{config.subtitle}</p>
          <p className="text-slate-400 text-xs leading-tight mb-2">{config.description}</p>
          <p className="text-xs text-slate-500 font-medium">{config.personality}</p>
        </div>
      </div>

      <div className="flex-1 overflow-y-auto space-y-4 custom-scrollbar">
        <AnimatePresence>
          {allRounds.length === 0 ? (
            <motion.div className="text-center py-12">
              <UserIcon className="h-16 w-16 mx-auto text-slate-600 mb-4" />
              <p className="text-slate-400 font-medium">Awaiting debate...</p>
            </motion.div>
          ) : (
            allRounds.map(round => (
              <RoundSection
                key={round}
                round={round}
                messages={messagesByRound[round] || []}
                isExpanded={expandedRounds.has(round)}
                onToggleExpansion={toggleRoundExpansion}
                config={config}
                isCurrentRound={round === currentRound}
                isWaitingForResponse={debateStatus === 'debating' && round === currentRound && !messagesByRound[round]}
              />
            ))
          )}
        </AnimatePresence>
      </div>

      <motion.div className="mt-6 pt-4 border-t border-slate-600/30 flex-shrink-0">
        <div className="flex justify-between items-center text-xs">
          <span className="text-slate-400"><span className={`${config.colors.primary} font-bold`}>{agentMessages.length}</span> responses</span>
          <span className="text-slate-400 font-medium">Round <span className={config.colors.primary}>{currentRound || '-'}</span></span>
        </div>
      </motion.div>
    </motion.div>
  );
};

const RoundSection = ({ round, messages, isExpanded, onToggleExpansion, config, isCurrentRound, isWaitingForResponse }) => {
  const hasMessages = messages && messages.length > 0;
  
  return (
    <motion.div className="border border-slate-600/30 rounded-2xl overflow-hidden backdrop-blur-sm">
      <button
        onClick={() => onToggleExpansion(round)}
        className={`w-full px-6 py-4 flex items-center justify-between transition-all duration-300 ${
          isCurrentRound 
            ? `${config.colors.accent} ${config.colors.primary} font-bold border-b ${config.colors.border}` 
            : 'bg-slate-800/30 text-slate-300 hover:bg-slate-700/50'
        }`}
      >
        <div className="flex items-center space-x-4">
          <div className={`w-3 h-3 rounded-full ${isCurrentRound ? config.colors.particle : 'bg-slate-500'}`} />
          <span className="font-bold">Round {round}</span>
        </div>
        <div className="flex items-center space-x-3">
          {hasMessages && (
            <span className="text-xs opacity-70 bg-slate-700/50 px-2 py-1 rounded-full">{messages.length} response{messages.length !== 1 ? 's' : ''}</span>
          )}
          <motion.div animate={{ rotate: isExpanded ? 180 : 0 }} transition={{ duration: 0.2 }}>
            <ChevronDownIcon className="h-5 w-5" />
          </motion.div>
        </div>
      </button>

      <AnimatePresence>
        {isExpanded && (
          <motion.div
            initial={{ height: 0, opacity: 0 }}
            animate={{ height: 'auto', opacity: 1 }}
            exit={{ height: 0, opacity: 0 }}
            transition={{ duration: 0.3, ease: "easeInOut" }}
            className="overflow-hidden"
          >
            <div className="p-6 bg-slate-800/20 space-y-4">
              {isWaitingForResponse ? (
                <div className="text-center py-8"><p className="text-sm text-slate-400">Analyzing...</p></div>
              ) : !hasMessages ? (
                <div className="text-center py-8"><p className="text-sm text-slate-500 italic">No response for this round</p></div>
              ) : (
                messages.map((message, index) => (
                  <motion.div
                    key={message.id || `${round}-${index}`}
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: index * 0.1 }}
                    className="bg-slate-900/50 rounded-xl p-4 border border-slate-600/20"
                  >
                    <p className="text-slate-200 leading-relaxed font-medium">{message.data.content}</p>
                  </motion.div>
                ))
              )}
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </motion.div>
  );
};

export default AgentPanel;
